import { useState } from 'react';

export default ({ deneme }) => {
	const handleMoreOptions = () => {
		if (styleClass === 'passive') setStyleClass('active');
		else setStyleClass('passive');
	};

	const [styleClass, setStyleClass] = useState('passive');

	return (
		<div>
			<br />
			<br />
			<br />

			<div style={{ padding: '100px' }}>
				<div className="field">
					<label
						className="label has-text-weight-normal has-text-link"
						style={{ cursor: 'pointer', userSelect: 'none' }}
						id="firstMoreOptions"
						onClick={handleMoreOptions}>
						<i className={styleClass === 'passive' ? 'fas fa-caret-right' : 'fas fa-caret-down'}></i>
						&nbsp; More options:
					</label>
				</div>

				<div>
					<div style={{ transition: '300ms' }} className={styleClass}>
						{deneme.map(person => (
							<label className="checkbox is-block">
								&emsp;
								<input type="checkbox" />
								&nbsp;{person}
							</label>
						))}

						{/* <label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Yes-No-Maybe poll.
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Anonymous opinion poll
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Participants can only vote for one option
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Display participants in alphabetical order
					</label> */}
					</div>
				</div>
			</div>
			<br />
			<br />
			<br />

			<style jsx>{`
				.passive {
					visibility: 'hidden';
					opacity: 0;
					height: 0;
				}

				.active {
					visibility: 'visible';
					opacity: 1;
					height: 120px;
				}
			`}</style>
		</div>
	);
};
