import { useRouter } from 'next/router';
import { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import axiosInstance from '../../config';

export default () => {
	const router = useRouter();
	const [itemDetails, setItemDetails] = useState({});

	const getPlan = async () => {
		const doc = await axiosInstance.get('api', { params: { itemID: router.query.id } });
		setItemDetails(doc.data);
	};
	useEffect(() => {
		getPlan();
		// console.log("l")
	}, []);
	return (
		<Layout>
			<div className="container">
				<br />
				<div className="columns">
					<div className="column is-1"></div>
					<div className="column">
						<div className="is-fullwidth" style={{ padding: '.5rem', backgroundColor: '#C8E4FF' }}>
							<h1
								onClick={() => {
									console.log(itemDetails.nameGenerater);
								}}>
								bas g√∂r
							</h1>
							<p>{/* Poll by <strong>{itemDetails.data.nameGenerater}</strong> */}</p>
						</div>
					</div>
					<div className="column is-1"></div>
				</div>
			</div>
		</Layout>
	);
};

