import { useState } from 'react';

export default ({ options }) => {
	const handleMoreOptions = () => {
		styleClass === 'passive' ? setStyleClass('active') : setStyleClass('passive');
	};

	const [styleClass, setStyleClass] = useState('passive');

	return (
		<div>
			<div>
				<div className="field">
					<label
						className="label has-text-weight-normal has-text-link"
						style={{ cursor: 'pointer', userSelect: 'none' }}
						id="firstMoreOptions"
						onClick={handleMoreOptions}>
						<i className={styleClass === 'passive' ? 'fas fa-caret-right' : 'fas fa-caret-down'}></i>
						&nbsp; More options:
					</label>
				</div>

				<div>
					<div style={{ transition: '300ms' }} className={styleClass}>
						{options.map(person => (
							<label className="checkbox is-block">
								&emsp;
								<input type="checkbox" />
								&nbsp;{person}
							</label>
						))}
					</div>
				</div>
			</div>

			<style jsx>{`
				.passive {
					visibility: 'hidden';
					opacity: 0;
					height: 0;
				}

				.active {
					visibility: 'visible';
					opacity: 1;
					height: 10%;
				}
			`}</style>
		</div>
	);
};
