import React, { useState } from 'react';
import ReactDOM from 'react-dom';

export default ({ getPossibleDates }) => {
	const [possibleDates, setPossibleDates] = useState(['', '']);
	const [itemCounter, setItemCounter] = useState(0);

	const handleDelete = e => {
		e.target.parentElement.remove();
	};

	const onPossibleDatesChanged = () => {
		alert('o');
	};

	return (
		<div>
			<br />
			<div className="columns">
				<div className="column"></div>
				<div className="column is-5">
					<div className="box" style={{ backgroundColor: '#C8E4FF' }}>
						<div className="field">
							<label className="label has-text-weight-normal">Enter possible dates below</label>
							<div className="field-body">
								<div className="field is-expanded">
									{possibleDates.map(item => {
										return (
											<div>
												<div className="field has-addons possible-dates-field">
													<p className="control is-expanded">
														<input
															className="input possible-dates-input"
															type="text"
															onChange={onPossibleDatesChanged}
														/>
														<span>{itemCounter}</span>
													</p>

													<p className="control" onClick={handleDelete}>
														<a className="button is-static">
															<i className="fas fa-trash-alt"></i>
														</a>
													</p>
												</div>
												<br />
												{setItemCounter((itemCounter += 1))}
											</div>
										);
									})}

									<p
										onClick={() => {
											setPossibleDates([...possibleDates, '']);
										}}>
										click here to add new one
									</p>

									<p
										onClick={() => {
											possibleDates[2] = 'aww';
											console.log(possibleDates);
										}}>
										click here to change one there
									</p>
								</div>
							</div>
						</div>

						<br />

						<a
							className="button is-small is-link"
							onClick={() => {
								getPossibleDates(possibleDates);
							}}>
							Next
						</a>
					</div>
				</div>
				<div className="column"></div>
			</div>
			<br />
			<br />
		</div>
	);
};
