import Layout from '../components/Layout';

const handleMoreOptions = () => {
	// alert('l');
};
var styleClassFirst = 2;

export default ({ deneme }) => (
	<Layout>
		<br />
		<br />
		<br />

		<div style={{ padding: '100px' }}>
			<div className="field">
				<label
					className="label has-text-weight-normal has-text-link"
					style={{ cursor: 'pointer', userSelect: 'none' }}
					id="firstMoreOptions"
					onClick={handleMoreOptions}>
					<i className={styleClassFirst === 'passive' ? 'fas fa-caret-right' : 'fas fa-caret-down'}></i>
					&nbsp; More options:
				</label>
			</div>

			<div>
				<div style={{ transition: '300ms' }} className={styleClassFirst}>
					<ul>
						{deneme.forEach(element => (
							<li>{element}</li>
						))}
					</ul>

					<ul>
						{deneme.map(person => (
							<li>{person}</li>
						))}
					</ul>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Yes-No-Maybe poll.
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Anonymous opinion poll
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Participants can only vote for one option
					</label>
					<label className="checkbox is-block">
						&emsp;
						<input type="checkbox" />
						&nbsp;Display participants in alphabetical order
					</label>
				</div>
			</div>
		</div>
		<br />
		<br />
		<br />
	</Layout>
);
